glyphication <- function (name) {
  glyph= vector()
  for (i in 1:length(name)){
    glyph[i]="triangle-up"
    if (name[i]=="Md") { glyph[i]="diamond-open" }
    else if (name[i]=="ML") { glyph[i]="circle-open" }
    else if (name[i]=="Mw") { glyph[i]="square-open" }
    else if (name[i]=="Mx") { glyph[i]="x-open" }
  }
  return(glyph)
}
