rmd2rscript <- function(infile, outfile){
  # read the file
  flIn <- readLines(infile)

  # double-check
  print(paste("Input File: ", infile, " Output File: ", outfile)) 
  print(paste("Line 4: ", flIn[4]))

  print("identify the start of code blocks")
  cdStrt <- which(grepl(flIn, pattern = "```{r*", perl = TRUE))

  print("identify the end of code blocks")
  cdEnd <- sapply(cdStrt, function(x){
    preidx <- which(grepl(flIn[-(1:x)], pattern = "```", perl = TRUE))[1]
    return(preidx + x)
  })

  print("define an expansion function")
  print("strip code block indacators")
  flIn[c(cdStrt, cdEnd)] <- ""
  expFun <- function(strt, End){
    strt <- strt+1
    End <- End-1
    return(strt:End)
  }

  idx <- unlist(mapply(FUN = expFun, strt = cdStrt, End = cdEnd, 
                SIMPLIFY = FALSE))

  print("add comments to all lines except code blocks")
  comIdx <- 1:length(flIn)
  comIdx <- comIdx[-idx]
  for(i in comIdx){
    flIn[i] <- paste("#' ", flIn[i], sep = "")
  }

  print("create an output file")
  nm <- strsplit(infile, split = "//.")[[1]][1]
  flOut <- file(outfile, "w")

  for(i in 1:length(flIn)){
    cat(flIn[i], "\n", file = flOut, sep = "\t")
  }

  print(paste("Output File (", outfile, ") ", "Line 4: ", flIn[4]))

  close(flOut) 
}
